mapper.select:
select

from sys_role


		and `name` = #{param.name}


		and `type` = #{param.type}


		and status = #{param.status}


		and (
		`type` like concat('%',#{param.keyWords},'%')
		or
		`name` like concat('%',#{param.keyWords},'%')
		or
		remark like concat('%',#{param.keyWords},'%')
		)


order by create_time desc

select count(1)
from sys_role


		and `name` = #{param.name}


		and `type` = #{param.type}


		and status = #{param.status}


		and (
		`type` like concat('%',#{param.keyWords},'%')
		or
		`name` like concat('%',#{param.keyWords},'%')
		or
		remark like concat('%',#{param.keyWords},'%')
		)
