mapper.update:
update sys_user
set password = #{param.newPassword}
where surrogate_id = #{param.surrogateId}
	and password = #{param.oldPassword}

mapper.select:
select surrogate_id, token, account, user_name, email
from sys_user


		and token = #{param.token}


		and account = #{param.account}


		and email = #{param.email}


		and password = #{param.password}

select

from sys_user

	account = #{param.account}
	and password = #{param.password}

select a.surrogate_id, a.token, a.`number`, a.account, a.user_name, a.telephone, a.email, a.org_id, a.status, a.deleted, a.remark, a.operate_ip, a.creator_id, a.operator, a.create_time, a.update_time, b.name as 'orgName', c.user_name as 'creator_name', d.account as 'operatorName' from sys_user as a inner join sys_org as b on (a.org_id = b.surrogate_id) inner join sys_user as c on (a.creator_id = c.surrogate_id) inner join sys_user as d on (a.operator = d.surrogate_id)
and a.org_id = #{param.surrogateId}
and a.user_name = #{param.userName}
and a.telephone = #{param.telephone}
and ( a.`number` like concat('%',#{param.keyWords},'%') or a.`user_name` like concat('%',#{param.keyWords},'%') or a.remark like concat('%',#{param.keyWords},'%') or b.name like concat('%',#{param.keyWords},'%') )
order by a.create_time desc

select count(1) from sys_user as a inner join sys_org as b on (a.org_id = b.surrogate_id) inner join sys_user as c on (a.creator_id = c.surrogate_id) inner join sys_user as d on (a.operator = d.surrogate_id)
and a.org_id = #{param.surrogateId}
and a.user_name = #{param.userName}
and a.telephone = #{param.telephone}
and ( a.`number` like concat('%',#{param.keyWords},'%') or a.`user_name` like concat('%',#{param.keyWords},'%') or a.remark like concat('%',#{param.keyWords},'%') or b.name like concat('%',#{param.keyWords},'%') or a.user_name like concat('%',#{param.keyWords},'%') )

SELECT account,
       email,
       user_name,
       COUNT(1) as 'count'
FROM sys_user
WHERE account = #{param.account}
	 OR email = #{param.email}
	 OR user_name = #{param.userName}
GROUP BY account, email, user_name;

select a.surrogate_id, a.account, a.user_name, a.remark, a.create_time, a.update_time from sys_user as a
a.status = 0 and a.surrogate_id in (
#{userId}
)
order by a.create_time desc

mapper.update:
update sys_user
set avatar = #{param.avatar}
where surrogate_id = #{param.userId}
