mapper.update:
update sys_admin
set password = #{param.newPassword}
where id = #{param.id}
	and password = #{param.oldPassword}

mapper.select:
select surrogate_id, token, account, user_name, email
from sys_admin


		and token = #{param.token}


		and account = #{param.account}


		and email = #{param.email}


		and password = #{param.password}

select 
from 


		and account = #{param.account}


		and password = #{param.password}

select a.id, a.token, a.`number`, a.account, a.user_name, a.telephone, a.email, a.org_id, a.status, a.deleted, a.remark, a.operate_ip, a.create_id, a.update_id, a.create_time, a.update_time, b.name as 'orgName', c.user_name as 'creator_name', d.account as 'operatorName' from sys_admin as a inner join sys_org as b on (a.org_id = b.surrogate_id) inner join sys_admin as c on (a.create_id = c.id) inner join sys_admin as d on (a.update_id = d.id)
and a.org_id = #{param.id}
and a.user_name = #{param.userName}
and a.telephone = #{param.telephone}
and ( a.`number` like concat('%',#{param.keyWords},'%') or a.`user_name` like concat('%',#{param.keyWords},'%') or a.remark like concat('%',#{param.keyWords},'%') or b.name like concat('%',#{param.keyWords},'%') )
order by a.create_time desc

select count(1) from sys_admin as a inner join sys_org as b on (a.org_id = b.surrogate_id) inner join sys_admin as c on (a.create_id = c.id) inner join sys_admin as d on (a.update_id = d.id)
and a.org_id = #{param.id}
and a.user_name = #{param.userName}
and a.telephone = #{param.telephone}
and ( a.`number` like concat('%',#{param.keyWords},'%') or a.`user_name` like concat('%',#{param.keyWords},'%') or a.remark like concat('%',#{param.keyWords},'%') or b.name like concat('%',#{param.keyWords},'%') or a.user_name like concat('%',#{param.keyWords},'%') )

SELECT account,
       email,
       user_name,
       COUNT(1) as 'count'
FROM sys_admin
WHERE account = #{param.account}
	 OR email = #{param.email}
	 OR user_name = #{param.userName}
GROUP BY account, email, user_name;

select a.id, a.account, a.user_name, a.remark, a.create_time, a.update_time from sys_admin as a
a.status = 0 and a.id in (
#{adminId}
)
order by a.create_time desc

mapper.update:
update sys_admin
set avatar = #{param.avatar}
where id = #{param.adminId}
